package com.icss.test;

import java.sql.SQLException;
import java.util.ArrayList;

import org.junit.Test;

import com.icss.hr.common.Pager;
import com.icss.hr.dept.pojo.Dept;
import com.icss.hr.emp.dao.EmpDao;
import com.icss.hr.emp.pojo.Emp;
import com.icss.hr.job.pojo.Job;

/**
 * 
 * @author Administrator
 *
 */
public class TestEmpDao {

	private EmpDao dao = new EmpDao();

	@Test
	public void testInsert() throws SQLException {

		Dept dept = new Dept();
		dept.setDeptId(10);

		Job job = new Job();
		job.setJobId(7);

		Emp emp = new Emp("tom", "tom", "123456", "tom@163.com", "13012345678"
				, 4500.0, dept, job, null, "爱好运动音乐美食");
		
		dao.insert(emp);

	}
	
	@Test
	public void testInsertMany() throws SQLException {

		Dept dept = new Dept();
		dept.setDeptId(40);

		Job job = new Job();
		job.setJobId(48);

		for (int i = 1; i <= 20;i ++) {
			Emp emp = new Emp("王五" + i, "wangwu" + i, "123456", "wangwu@163.com", "13912345678"
					, 7200.0, dept, job, null, "无");
			
			dao.insert(emp);
		}

	}
	
	@Test
	public void testQueryByLoginName() throws SQLException {
		
		Emp emp = dao.queryByLoginName("tom");
		
		System.out.println(emp);				
	}
	
	@Test
	public void testGetCount() throws SQLException {
		
		int count = dao.getCount();		
		System.out.println("count=" + count);
		
	}
	
	@Test
	public void testQueryByPage() throws SQLException {
		
		Pager pager = new Pager(dao.getCount(),10,1);
		ArrayList<Emp> list = dao.queryByPage(pager);
		
		list.forEach(System.out::println);		
	}
	
	@Test
	public void testQueryById() throws SQLException {
		Emp emp = dao.queryById(1111);
		System.out.println(emp);
	}

	@Test
	public void testUpdate() throws SQLException {
		Dept dept = new Dept();
		dept.setDeptId(420);
		
		Job job = new Job();
		job.setJobId(27);
		
		Emp emp = new Emp(30,"汤姆克鲁斯", "kelusi", "123456", "kelusi@163.com", "13612345678"
				, 8888.88, dept, job, null, "著名演员");
		
		dao.update(emp);
	}
	
	@Test
	public void testDelete() throws SQLException {
		
		dao.delete(28);
		
	}
	
	@Test
	public void testUpdateEmpPic() throws SQLException {
		
		String dataUrl = "data:image/jpeg;base64,";
		
		dao.updateEmpPic("tom", dataUrl);
		
	}
	
	@Test
	public void testQueryEmpPic() throws SQLException {
		
		String dataUrl = dao.queryEmpPic("zhangfei");
		
		System.out.println(dataUrl);
		
	}
	
	@Test
	public void testUpdatePwd() throws SQLException {
		
		Emp emp = new Emp();
		emp.setEmpLoginName("tom");
		emp.setEmpPwd("666666");
		
		dao.updatePwd(emp);		
	}
	
}